import React, { useContext } from 'react';
import CarsContext from './context/CarsContext';
import carBlue from './images/carBlue.jpeg';
import carRed from './images/carRed.jpeg';
import carYellow from './images/carYellow.jpeg';

const Cars = () => {
  const { cars, setCars} = useContext(CarsContext);

  const moveCar = (car, side) => {
    setCars({ ...cars, [car]: side });
  }
  return (
    <div>
        <div>
          <img
            className={cars.redCar ? 'car-right' : 'car-left'}
            src={carRed}
            alt="red car"
          />
          <button
            onClick={() => moveCar('redCar', !cars.redCar)}
            type="button"
          >
            Move
        </button>
        </div>
        <div>
          <img
            className={cars.blueCar ? 'car-right' : 'car-left'}
            src={carBlue}
            alt="blue car"
          />
          <button
            onClick={() => moveCar('blueCar', !cars.blueCar)}
            type="button"
          >
            Move
        </button>
        </div>
        <div>
          <img
            className={cars.yellowCar ? 'car-right' : 'car-left'}
            src={carYellow}
            alt="yellow car"
          />
          <button
            onClick={() => moveCar('yellowCar', !cars.yellowCar)}
            type="button"
          >
            Move
        </button>
        </div>
      </div>
  )
}

export default Cars;
