import React, { useContext } from 'react';
import './GameCell.css';
import xImage from './x.png';
import oImage from './o.png';
import TicTacToeContext from './context/TicTacToeContext';

const GameCell = ({ content, id }) => {
  const { game, setGame } = useContext(TicTacToeContext);

  const toggleActivePlayer = () => {
    const { activePlayer } = this.state;
    if (activePlayer === 1) return 2;
    return 1;
  }

  const onClick = (cellClicked) => {
    const newState = [...game.gameBoard];
      let newActivePlayer = game.activePlayer;

      if (game.gameBoard[cellClicked] === 0) {
        newState[cellClicked] = game.activePlayer;
        newActivePlayer = toggleActivePlayer();
      } else newState[cellClicked] = game.gameBoard[cellClicked];
    setGame(
      { 
      activePlayer: newActivePlayer,
      gameBoard: newState, 
      })
  }
    if (content === 1) {
      return (
        <div
          data-testid={`cell_${id}`}
          className="game-cell"
          onClick={onClick}
          role="button"
          tabIndex="0"
          aria-label="Cell"
          onKeyPress={onClick}
        >
          <img data-testid={`cell_${id}_image`} alt="X" src={xImage} />
        </div>
      );
    }
    if (content === 2) {
      return (
        <div
          data-testid={`cell_${id}`}
          className="game-cell"
          onClick={onClick}
          role="button"
          tabIndex="0"
          aria-label="Cell"
          onKeyPress={onClick}
        >
          <img data-testid={`cell_${id}_image`} alt="O" src={oImage} />
        </div>
      );
    }
    return (
      <div
        role="button"
        tabIndex="0"
        aria-label="Cell"
        onKeyPress={onClick}
        data-testid={`cell_${id}`}
        className="game-cell"
        onClick={onClick}
      />
    );
}

export default GameCell;
